import React, { useState } from 'react';
import '../../assets/styles/global.css';
import Menu from '../../components/menu';
import './style-home.css';


export default function Home() {

    const [valueOne, setValueOne] = useState('');
    const [valueTwo, setValueTwo] = useState('');

    const [selectOne, setSelectOne] = useState('');
    const [selectTwo, setSelectTwo] = useState('');

    const teste =() => {

        switch (selectOne) {
            case "USD":
                if (selectTwo == "BRL") {
                    const calculo = parseFloat(valueOne) / 2.21;
                    
                }
                break;
        
            default:
                break;
        }

        console.log(selectOne);
        console.log(selectTwo);
    }

    return (
        <div className="container-home">
            <div className="menu">
                <Menu />
            </div>
            <div className="align-input">
                <div className="first-input">
                    <select className="select-moeda" name="moedas" onChange={e => setSelectOne(e.target.value)} >
                        <option value="none" selected disabled>Escolha uma moeda</option>
                        <option value="USD">USD - Dólar</option>
                        <option value="BRL">BRL - Real</option>
                        <option value="EUR">EUR - Euro</option>
                        <option value="BTC">BTC - Bitcoin</option>
                    </select>
                    <input id="input-edit" type="text" placeholder="00.00" onChange={e => setValueOne(e.target.value)} />
                </div>
                <h1>Para</h1>
                <div className="second-input">
                    <select className="select-moeda" name="moedas" onChange={e => setSelectTwo(e.target.value)}>
                        <option value="none" selected disabled>Escolha uma moeda</option>
                        <option value="USD">USD - Dólar</option>
                        <option value="BRL">BRL - Real</option>
                        <option value="EUR">EUR - Euro</option>
                        <option value="BTC">BTC - Bitcoin</option>
                    </select>
                    <input id="input-edit" type="text" placeholder="00.00" onChange={e => setValueTwo(e.target.value)} />
                </div>
                <input type="button" onClick={teste} name="TESTEEEEEEE"/>
            </div>
        </div>
    );
}
