import React, { useEffect, useState } from 'react';
import '../../assets/styles/global.css';
import Menu from '../../components/menu';
import './style-home.css';


export default function Home() {

    const [valueOne, setValueOne] = useState('');
    const [valueTwo, setValueTwo] = useState('');

    const [selectOne, setSelectOne] = useState('');
    const [selectTwo, setSelectTwo] = useState('');

    const [currency, setCurrency] = useState([]);

    useEffect(() => {
        listar();
    }, []);
    
    const url = 'https://api.exchangeratesapi.io/latest';

    const listar = () => {
        fetch(url, {
            method: 'GET'
        })
            .then(response => response.json())
            .then(moedas => {
                setCurrency(moedas);
                console.log(moedas);
                console.log(Object.values(moedas[2]))
            })
            .catch(Erro => console.log(Erro));
    }

    const converting = () => {
        // Caso o usuario coloque o valor no primeiro input
        switch (selectOne) {
            case "USD":
                if (selectTwo == "BRL") {
                    const calculo = parseFloat(valueOne) * 5.31;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "EUR") {
                    const calculo = parseFloat(valueOne) * 0.82;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "BTC") {
                    const calculo = parseFloat(valueOne) * 0.000026;
                    const formater = calculo.toFixed(8);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "USD") {
                    var text = "Mesmo valor"
                    setValueTwo(text);
                }
                break;

            case "BRL":
                if (selectTwo == "USD") {
                    const calculo = parseFloat(valueOne) / 5.31;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "EUR") {
                    const calculo = parseFloat(valueOne) * 0.15;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "BTC") {
                    const calculo = parseFloat(valueOne) * 0.0000049;
                    const formater = calculo.toFixed(8);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "BRL") {
                    var text = "Mesmo valor"
                    setValueTwo(text);
                }
                break;

            case "EUR":
                if (selectTwo == "USD") {
                    const calculo = parseFloat(valueOne) / 0.82;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "BRL") {
                    const calculo = parseFloat(valueOne) * 6.56;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "BTC") {
                    const calculo = parseFloat(valueOne) * 0.000032;
                    const formater = calculo.toFixed(8);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "EUR") {
                    var text = "Mesmo valor"
                    setValueTwo(text);
                }
                break;

            case "BTC":
                if (selectTwo == "USD") {
                    const calculo = parseFloat(valueOne) * 38519;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "BRL") {
                    const calculo = parseFloat(valueOne) * 206288.50;
                    const formater = calculo.toFixed(2);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "EUR") {
                    const calculo = parseFloat(valueOne) * 31420.53;
                    const formater = calculo.toFixed(8);
                    setValueTwo(formater + '');
                }
                else if (selectTwo == "BTC") {
                    var text = "Mesmo valor"
                    setValueTwo(text);
                }
                break;
            default:
                break;
        }
    }

    return (
        <div className="container-home">
            <div className="menu">
                <Menu />
            </div>
            <div className="align-web">
                <div className="align-input">
                    <div className="first-input">
                        <select className="select-moeda" name="moedas" onChange={e => setSelectOne(e.target.value)} >
                            <option value="none" selected disabled>Escolha uma moeda</option>
                            <option value="USD">USD - Dólar</option>
                            <option value="BRL">BRL - Real</option>
                            <option value="EUR">EUR - Euro</option>
                            <option value="BTC">BTC - Bitcoin</option>
                        </select>

                    </div>
                    <h1>Para</h1>
                    <div className="second-input">
                        <select className="select-moeda" name="moedas" onChange={e => setSelectTwo(e.target.value)}>
                            <option value="none" selected disabled>Escolha uma moeda</option>
                            <option value="USD">USD - Dólar</option>
                            <option value="BRL">BRL - Real</option>
                            <option value="EUR">EUR - Euro</option>
                            <option value="BTC">BTC - Bitcoin</option>
                        </select>

                    </div>
                </div>
                <div className="align-button">
                    <input id="input-edit" type="text" placeholder="Valor a ser convertido" onChange={e => setValueOne(e.target.value)} value={valueOne} />
                    <input type="button" onClick={converting} value="Converter" />
                    <input id="input-edit" type="text" disabled placeholder="Resultado" onClick={converting} value={valueTwo} />
                </div>
            </div>
            <div className="align-mobile">
                <div className="align-input">
                    <div className="first-input">
                        <select className="select-moeda" name="moedas" onChange={e => setSelectOne(e.target.value)} >
                            <option value="none" selected disabled>Escolha uma moeda</option>
                            <option value="USD">USD - Dólar</option>
                            <option value="BRL">BRL - Real</option>
                            <option value="EUR">EUR - Euro</option>
                            <option value="BTC">BTC - Bitcoin</option>
                        </select> <br />
                        <input id="input-edit" type="text" placeholder="Valor a ser convertido" onChange={e => setValueOne(e.target.value)} value={valueOne} />
                    </div>
                    <div className="second-input">
                        <select className="select-moeda" name="moedas" onChange={e => setSelectTwo(e.target.value)}>
                            <option value="none" selected disabled>Escolha uma moeda</option>
                            <option value="USD">USD - Dólar</option>
                            <option value="BRL">BRL - Real</option>
                            <option value="EUR">EUR - Euro</option>
                            <option value="BTC">BTC - Bitcoin</option>
                        </select> <br />
                        <input id="input-edit" type="text" disabled placeholder="Resultado" onClick={converting} value={valueTwo} />

                    </div>
                    <div className="align-button">
                        <input type="button" onClick={converting} value="Converter" />
                    </div>
                </div>
            </div>
        </div>
    );
}
